#!/bin/bash

# MusicMart 100% FREE AWS Deployment Script
# This script deploys a complete e-commerce platform for $0.00/month
# Uses only AWS Free Tier resources

set -e

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
PURPLE='\033[0;35m'
CYAN='\033[0;36m'
NC='\033[0m' # No Color

# Configuration
PROJECT_NAME="legato"
AWS_REGION="ap-south-1"
INSTANCE_TYPE="t2.micro"
KEY_NAME="legato-key"
TERRAFORM_DIR="terraform"

# Functions
print_header() {
    echo -e "${PURPLE}🎵 $1${NC}"
}

print_success() {
    echo -e "${GREEN}✅ $1${NC}"
}

print_warning() {
    echo -e "${YELLOW}⚠️  $1${NC}"
}

print_error() {
    echo -e "${RED}❌ $1${NC}"
}

print_info() {
    echo -e "${BLUE}📋 $1${NC}"
}

print_cost() {
    echo -e "${CYAN}💰 $1${NC}"
}

# Start deployment
print_header "Starting Legato 100% FREE deployment to AWS..."
print_cost "This deployment costs \$0.00/month within AWS Free Tier"

# Check prerequisites
print_info "Checking AWS credentials..."
if ! aws sts get-caller-identity > /dev/null 2>&1; then
    print_error "AWS credentials not configured. Please run 'aws configure'"
    exit 1
fi

ACCOUNT_ID=$(aws sts get-caller-identity --query Account --output text)
print_success "AWS credentials verified for account: $ACCOUNT_ID"

# Check if SSH key exists
if [ ! -f ~/.ssh/id_rsa ]; then
    print_warning "SSH key not found. Generating new key pair..."
    ssh-keygen -t rsa -b 2048 -f ~/.ssh/id_rsa -N ""
    print_success "SSH key generated at ~/.ssh/id_rsa"
else
    print_success "SSH key found at ~/.ssh/id_rsa"
fi

# Import SSH key to AWS if it doesn't exist
if ! aws ec2 describe-key-pairs --key-names "$KEY_NAME" > /dev/null 2>&1; then
    print_info "Importing SSH key to AWS..."
    aws ec2 import-key-pair \
        --key-name "$KEY_NAME" \
        --public-key-material fileb://~/.ssh/id_rsa.pub \
        --region "$AWS_REGION"
    print_success "SSH key imported to AWS as $KEY_NAME"
else
    print_success "SSH key $KEY_NAME already exists in AWS"
fi

# Navigate to terraform directory
cd "$TERRAFORM_DIR"

# Initialize Terraform
print_info "Initializing Terraform..."
terraform init

# Plan deployment
print_info "Planning Terraform deployment..."
terraform plan \
    -var="aws_region=$AWS_REGION" \
    -var="project_name=$PROJECT_NAME" \
    -var="instance_type=$INSTANCE_TYPE" \
    -var="key_name=$KEY_NAME" \
    -var="environment=prod" \
    -out=tfplan

# Apply deployment
print_info "Deploying infrastructure..."
terraform apply tfplan

# Get outputs
print_info "Retrieving deployment information..."
APPLICATION_URL=$(terraform output -raw application_url)
APPLICATION_IP=$(terraform output -raw application_ip)
SSH_COMMAND=$(terraform output -raw ssh_command)
S3_BUCKET=$(terraform output -raw s3_bucket_name)
HEALTH_CHECK_URL=$(terraform output -raw health_check_url)

# Wait for instance to be ready
print_info "Waiting for application to be ready..."
echo "This may take 5-10 minutes for the complete setup..."

# Function to check if application is ready
check_application() {
    local max_attempts=60
    local attempt=1
    
    while [ $attempt -le $max_attempts ]; do
        echo -n "Attempt $attempt/$max_attempts: "
        
        if curl -s --max-time 10 "$HEALTH_CHECK_URL" > /dev/null 2>&1; then
            print_success "Application is ready!"
            return 0
        else
            echo "Not ready yet, waiting 30 seconds..."
            sleep 30
            ((attempt++))
        fi
    done
    
    print_warning "Application may still be starting up. Check manually in a few minutes."
    return 1
}

# Check application readiness
check_application

# Display deployment summary
echo ""
print_header "🎉 DEPLOYMENT COMPLETED SUCCESSFULLY! 🎉"
echo ""
print_cost "💰 MONTHLY COST: \$0.00 (AWS Free Tier)"
echo ""
